																			null) = 'S'))) 	 -- nr_seq_exame_p
  and not exists (
				select	1
				from	prescr_rec_hor c
				where	c.nr_prescricao = x.nr_prescricao
				and	c.nr_seq_recomendacao = x.nr_sequencia
				and	c.nr_prescricao = a.nr_prescricao
				and	((ie_data_lib_prescr_p = 'M') or (Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'))) and ((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p)) group by
			a.nr_prescricao,
