			a.dt_liberacao_medico,
			a.dt_liberacao,
			a.dt_liberacao_farmacia,
			x.ie_se_necessario,
			x.ds_recomendacao,
			x.ie_acm
		) alias34
	group by
		nr_prescricao,
		nr_seq_recomendacao,
		cd_recomendacao,
		ds_recomendacao,
		ie_acm_sn,
		cd_intervalo,
		qt_recomendacao,
		ds_prescricao,
		ie_suspenso,
		ie_lib_pend_rep,
		ds_intervalo,
		ds_rec,
		ie_permite_acao;

	
BEGIN
	dt_fim_w	:= vipe_gerar_horarios_pck.getdtfimvalidade(dt_validade_limite_p);
	open c01;
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_recomendacao_w,
			cd_recomendacao_w,
			ds_recomendacao_w,
