				)*/
		and	obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'
		and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
		and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
		and	((ie_solucoes_continuas_p = 'S') or (coalesce(x.ie_urgencia,'N') = 'S') or (obter_se_sol_continua_vipe(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao,dt_inicial_horarios_p,dt_final_horarios_p) = 'N'))
		and	((ie_regra_inclusao_p = 'S') or
			 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SOL',
																			cd_estabelecimento_p,
																			cd_setor_usuario_p,
																			cd_perfil_p,
																			null,
																			null,
																			null,
																			null,
																			a.cd_setor_atendimento,
																			null,
																			null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e n達o haviam passado nada
																			null) = 'S'))) -- nr_seq_exame_p
		and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
		group by
			a.nr_prescricao,
			x.nr_seq_solucao,
			x.ds_solucao,
			x.ie_acm,
			x.ie_se_necessario,
			nm_usuario_p,
			cd_estabelecimento_p,
			--obter_desc_intervalo(x.ie_classif_agora),
			x.ie_suspenso,
			x.dt_prev_prox_etapa,
			x.ie_via_aplicacao,
			x.qt_solucao_total,
			substr(obter_desc_vol_etapa_adep2(x.nr_prescricao, x.nr_seq_solucao, x.ie_acm, x.ie_se_necessario),1,240),
			x.ie_urgencia,
			x.nr_etapas,
			x.qt_hora_fase,
			x.cd_unidade_medida,
			x.qt_volume,
			x.nr_prescricao_anterior,
