																		a.cd_setor_atendimento,
																		null,
																		null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e n達o haviam passado nada
																		null) = 'S'))) --nr_seq_exame_p
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	group by
			a.nr_prescricao,
			x.nr_seq_solucao,
			x.ds_solucao,
			x.ie_acm,
			x.ie_se_necessario,
			nm_usuario_p,
			cd_estabelecimento_p,
			x.ie_suspenso,
			--obter_desc_intervalo(x.ie_classif_agora),
			x.dt_prev_prox_etapa,
			--decode(x.ie_classif_agora,null,'',' - '),
			a.dt_liberacao_medico,
			a.dt_liberacao,
			a.dt_liberacao_farmacia,
			substr(obter_desc_vol_etapa_adep2(x.nr_prescricao, x.nr_seq_solucao, x.ie_acm, x.ie_se_necessario),1,240),
			x.ie_via_aplicacao,
			x.qt_solucao_total,
			x.ie_urgencia,
			x.nr_etapas,
			x.qt_hora_fase;

	
BEGIN
	SELECT * FROM vipe_gerar_horarios_pck.getdtrefsol(dt_validade_limite_p, dt_iniciosol_w, dt_fim_w) INTO STRICT dt_iniciosol_w, dt_fim_w;
	qt_existe_w	:= 0;
	ds_sep_bv_w	:= obter_separador_bv;
	if (getIeGrafico = 'N') then

		open c01;
		loop
		fetch c01 into	nr_prescricao_w,
				nr_seq_solucao_w,
				ds_solucao_w,
				ie_acm_sn_w,
				ds_prescricao_w,
				ds_componentes_w,
				ie_status_solucao_w,
				ie_suspensa_w,
				dt_prev_prox_etapa_w,
				cd_unid_med_qtde_w,
				ie_via_administracao_w,
				qt_solucao_total_w,
				ds_intervalo_w,
				nr_prescricao_anterior_w,
				nr_sequencia_anterior_w,
				ie_permite_acao_w,
				ie_urgencia_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

			qt_existe_w	:= 0;

			if (get_ie_agrupar_itens_iguais	= 'S') and (ie_solucoes_continuas_p	= 'N') and (ie_urgencia_w	=	'N') and
				((ie_agrupar_acm_sn_p		= 'S') or (ie_acm_sn_w			= 'N')) then

				select	count(nr_sequencia)
				into STRICT	qt_existe_w
				from	w_vipe_t
				where	nm_usuario	= nm_usuario_p
				and	ie_tipo_item	= 'SOL'
				and	ds_item		= ds_solucao_w
				and	ie_acm_sn	= ie_acm_sn_w
				and	coalesce(ds_prescricao,'XPTO')	= coalesce(ds_prescricao_w,'XPTO')
				and	((coalesce(ds_diluicao,'XPTO')	= coalesce(ds_componentes_w,'XPTO')) or
					 (nr_prescricao		= nr_prescricao_anterior_w AND nr_seq_item			= nr_sequencia_anterior_w))
--				and	nvl(ie_status_item,'N')		= nvl(ie_status_solucao_w,'N')
				and	coalesce(ie_suspenso,'N')		= coalesce(ie_suspensa_w,'N')
				and	coalesce(cd_unid_med_qtde,'XPTO')	= coalesce(cd_unid_med_qtde_w,'XPTO')
				and	coalesce(ie_via_aplicacao,'XPTO')	= coalesce(ie_via_administracao_w,'XPTO')
				and	coalesce(qt_item,0)			= coalesce(qt_solucao_total_w,0)
				and	coalesce(ds_interv_prescr,'XPTO')	= coalesce(ds_intervalo_w,'XPTO');

			end if;

			if (qt_existe_w	= 0) then

				select	nextval('w_vipe_t_seq')
				into STRICT	nr_seq_wadep_w
				;

				insert into w_vipe_t(
					nr_sequencia,
					nm_usuario,
					ie_tipo_item,
					nr_prescricao,
					nr_seq_item,
					ds_item,
					ie_acm_sn,
					ds_prescricao,
					ds_diluicao,
					ie_status_item,
					ie_suspenso,
					dt_prev_term,
					nr_seq_proc_interno,
					nr_agrupamento,
					ie_diferenciado,
					nr_prescricoes,
					cd_item,
					cd_unid_med_qtde,
					ie_via_aplicacao,
					qt_item,
					ds_interv_prescr,
					ie_permite_acao)
				values (
					nr_seq_wadep_w,
					nm_usuario_p,
					'SOL',
					nr_prescricao_w,
					nr_seq_solucao_w,
					SUBSTR(ds_solucao_w, 1, 240),
					ie_acm_sn_w,
					ds_prescricao_w,
					ds_componentes_w,
					ie_status_solucao_w,
					ie_suspensa_w,
					dt_prev_prox_etapa_w,
					0,
					0,
					'N',
					nr_prescricao_w,
					0,
					cd_unid_med_qtde_w,
					ie_via_administracao_w,
					qt_solucao_total_w,
					ds_intervalo_w,
					ie_permite_acao_w);
			end if;

			end;
		end loop;
		close c01;

		open C04;
		loop
		fetch C04 into
			nr_prescr_sol_w;
		EXIT WHEN NOT FOUND; /* apply on C04 */
			begin
			if (current_setting('vipe_gerar_horarios_pck.nr_prescricoes_w')::coalesce(varchar(4000)::text, '') = '') then
				PERFORM set_config('vipe_gerar_horarios_pck.nr_prescricoes_w', nr_prescr_sol_w, false);
			else
				PERFORM set_config('vipe_gerar_horarios_pck.nr_prescricoes_w', adep_juntar_prescricao(current_setting('vipe_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000),nr_prescr_sol_w), false);
			end if;
			end;
		end loop;
		close C04;

		open C05;
		loop
		fetch C05 into
			nr_prescr_sol_w,
			nr_seq_solucao_w,
			ds_solucao_w,
			ie_acm_sn_w,
			ds_prescricao_w,
			ds_componentes_w,
			ie_status_solucao_w,
			ie_suspensa_w,
			dt_prev_prox_etapa_w,
			current_setting('vipe_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
			cd_unid_med_qtde_w,
			ie_via_administracao_w,
			qt_solucao_total_w,
			ds_intervalo_w;
		EXIT WHEN NOT FOUND; /* apply on C05 */
			begin

			ds_comando_update_w	:=	' update w_vipe_t ' ||
							' set  nr_prescricoes = adep_juntar_prescricao(:nr_prescricoes,:nr_prescricao) ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and 	ie_tipo_item = :ie_tipo ' ||
							' and 	ds_item	= :ds_item '||
							' and 	ds_prescricao = :ds_prescricao '||
							' and 	nvl(ie_status_item,''N'') =  nvl(:ie_status_solucao,''N'') ' ||
							' and 	nvl(cd_unid_med_qtde,''XPTO'') = nvl(:cd_unid_med_qtde,''XPTO'') ' ||
							' and 	nvl(ds_interv_prescr,''XPTO'') = nvl(:ds_intervalo,''XPTO'') '||
							' and (((:ie_solucoes_continuas_p = ''S'') and '||
							'      (nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0)) and ' ||
							'      (nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0))) or ' ||
							'      ((:ie_solucoes_continuas_p = ''N'') and ' ||
							'	(nvl(ie_acm_sn,''N'')  <> ''S'') and ' ||
							'	(nvl(nr_prescricao,nvl(:nr_prescricao,0)) <> nvl(:nr_prescricao,0)) and' ||
							'       (obter_se_contido(nr_prescricao, ''('' || :nr_prescricoes || '')'') = ''S'')))';

			CALL exec_sql_dinamico_bv('VIPE', ds_comando_update_w,	'nr_prescricoes=' || current_setting('vipe_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000) || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(nr_prescr_sol_w) || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
										'ds_item='|| ds_solucao_w || ds_sep_bv_w ||
										'ds_prescricao='|| ds_prescricao_w || ds_sep_bv_w ||
										'ie_status_solucao='||ie_status_solucao_w || ds_sep_bv_w ||
										'cd_unid_med_qtde='|| cd_unid_med_qtde_w  || ds_sep_bv_w ||
										'ds_intervalo='|| ds_intervalo_w  || ds_sep_bv_w ||
										'ie_tipo=SOL' || ds_sep_bv_w ||
										'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
										'ie_solucoes_continuas_p=' || ie_solucoes_continuas_p || ds_sep_bv_w);


			end;
		end loop;
		close C05;

		if (ie_lib_pend_rep_p = 'S') then
			begin
			open c02;
			loop
			fetch c02 into	nr_prescricao_w,
					nr_seq_solucao_w,
					ds_solucao_w,
					ie_acm_sn_w,
					ds_prescricao_w,
					ds_componentes_w,
					ie_status_solucao_w,
					ie_suspensa_w,
					dt_prev_prox_etapa_w,
					current_setting('vipe_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
					cd_unid_med_qtde_w,
					ie_via_administracao_w,
					qt_solucao_total_w,
					ds_intervalo_w,
					ie_permite_acao_w;
			EXIT WHEN NOT FOUND; /* apply on c02 */
				begin
				select	nextval('w_vipe_t_seq')
				into STRICT	nr_seq_wadep_w
				;

				insert into w_vipe_t(
					nr_sequencia,
					nm_usuario,
					ie_tipo_item,
					nr_prescricao,
					nr_seq_item,
					ds_item,
					ie_acm_sn,
					ds_prescricao,
					ds_diluicao,
					ie_status_item,
					ie_suspenso,
					dt_prev_term,
					nr_seq_proc_interno,
					nr_agrupamento,
					ie_diferenciado,
					nr_prescricoes,
					ie_pendente_liberacao,
					cd_item,
					cd_unid_med_qtde,
					ie_via_aplicacao,
					qt_item,
					ds_interv_prescr,
					ie_permite_acao)
				values (
					nr_seq_wadep_w,
					nm_usuario_p,
					'SOL',
					nr_prescricao_w,
					nr_seq_solucao_w,
					SUBSTR(ds_solucao_w, 1, 240),
					ie_acm_sn_w,
					ds_prescricao_w,
					ds_componentes_w,
					ie_status_solucao_w,
					ie_suspensa_w,
					dt_prev_prox_etapa_w,
					0,
					0,
					'N',
					nr_prescricao_w,
					current_setting('vipe_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
					0,
					cd_unid_med_qtde_w,
					ie_via_administracao_w,
					qt_solucao_total_w,
					ds_intervalo_w,
					ie_permite_acao_w);
				end;
			end loop;
			close c02;
			end;
		end if;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vipe_gerar_horarios_pck.vipe_obter_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_solucoes_continuas_p text, ie_prescr_setor_p text, ie_formato_solucoes_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
