CREATE OR REPLACE PROCEDURE vipe_gerar_horarios_pck.vipe_sinc_diet_oral_serv ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_horarios_dietas_orais_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE


	ds_sep_bv_w		varchar(50);

	nr_prescricao_w		bigint;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(15);
	cd_refeicao_w		varchar(255);
	ds_informacoes_w	varchar(2000);
	ds_observacoes_w	varchar(4000);
	ie_suspenso_w		varchar(1);
	ds_informacoes_ww	varchar(2000);
	ds_comando_update_w	varchar(4000);
	current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	dt_fim_w		timestamp;
	ie_pintar_w		varchar(1);

	c01 CURSOR FOR
	SELECT 	a.nr_prescricao,
		x.nr_sequencia,
		CASE WHEN coalesce(x.dt_bloqueio::text, '') = '' THEN CASE WHEN coalesce(d.dt_suspensao::text, '') = '' THEN x.ie_status_adep  ELSE 'S' END   ELSE 'B' END ,
		x.nr_sequencia,
		obter_dietas_servico(x.nr_sequencia,1),
		x.ds_observacao,
		x.dt_servico dt_horario,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, d.nr_prescricao, d.nr_sequencia, 'D') ie_pintar,
		coalesce(d.ie_suspenso,'N')
	from	prescr_dieta d,
		nut_servico b,
		prescr_medica a,
		nut_atend_serv_dia x
	where	d.nr_prescricao	= a.nr_prescricao
	and 	x.nr_atendimento = a.nr_atendimento
	and	x.nr_seq_servico = b.nr_sequencia
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	Obter_se_servico_vig_dieta(a.dt_inicio_prescr, a.dt_validade_prescr, a.nr_Atendimento, '1', ie_exibir_hor_realizados_p, ie_exibir_hor_suspensos_p) = 'S'
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
	and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
	and 	((coalesce(b.IE_TIPO_PRESCRICAO_SERVICO::text, '') = '') or (b.IE_TIPO_PRESCRICAO_SERVICO = '1'))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(x.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	x.dt_servico  between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and x.ie_status_adep = 'Y'
	group by x.dt_servico,
		a.nr_prescricao,
		x.nr_sequencia,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, d.nr_prescricao, d.nr_sequencia, 'D'),
		CASE WHEN coalesce(x.dt_bloqueio::text, '') = '' THEN CASE WHEN coalesce(d.dt_suspensao::text, '') = '' THEN x.ie_status_adep  ELSE 'S' END   ELSE 'B' END ,
		x.ds_observacao,
		d.ie_suspenso
	
union

	SELECT 	a.nr_prescricao,
		x.nr_sequencia,
		CASE WHEN coalesce(x.dt_bloqueio::text, '') = '' THEN CASE WHEN coalesce(d.dt_suspensao::text, '') = '' THEN x.ie_status_adep  ELSE 'S' END   ELSE 'B' END ,
		x.nr_sequencia,
		obter_dietas_servico(x.nr_sequencia,1),
		x.ds_observacao,
		x.dt_servico,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, d.nr_prescricao, d.nr_sequencia, 'D') ie_pintar,
		coalesce(d.ie_suspenso,'N')
	from	prescr_dieta d,
		nut_servico b,
		nut_atend_serv_dia_rep c,
		prescr_medica a,
		nut_atend_serv_dia x
	where	d.nr_prescricao	= a.nr_prescricao
	and 	x.nr_atendimento = a.nr_atendimento
	and	x.nr_seq_servico = b.nr_sequencia
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.nr_prescricao = c.nr_prescr_oral
	and	c.nr_seq_serv_dia = x.nr_sequencia
	and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
	and	Obter_se_servico_vig_dieta(a.dt_inicio_prescr, a.dt_validade_prescr, a.nr_Atendimento, '1', ie_exibir_hor_realizados_p, ie_exibir_hor_suspensos_p) = 'S'
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(x.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and 	((coalesce(b.IE_TIPO_PRESCRICAO_SERVICO::text, '') = '') or (b.IE_TIPO_PRESCRICAO_SERVICO = '1'))
	and	x.dt_servico   between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and 	x.ie_status_adep <> 'Y'
	group by x.dt_servico,
		a.nr_prescricao,
		x.nr_sequencia,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, d.nr_prescricao, d.nr_sequencia, 'D'),
		CASE WHEN coalesce(x.dt_bloqueio::text, '') = '' THEN CASE WHEN coalesce(d.dt_suspensao::text, '') = '' THEN x.ie_status_adep  ELSE 'S' END   ELSE 'B' END ,
		x.ds_observacao,
		d.ie_suspenso
	order by dt_horario;

	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	dt_fim_w	:= vipe_gerar_horarios_pck.getdtfimvalidade(dt_validade_limite_p);
	open c01;
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_refeicao_w,
			ds_informacoes_w,
			ds_observacoes_w,
			current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp,
			ie_pintar_w,
			ie_suspenso_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := vipe_gerar_horarios_pck.obter_posicao_horario(current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp);

		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			ds_informacoes_ww	:= substr(ds_informacoes_w || ' - Obs: ' || ds_observacoes_w,1,2000);


			ds_comando_update_w	:=	' update w_vipe_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
							' ds_diluicao = :ds_informacoes, ' ||
							' ie_pintar = :ie_pintar ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and cd_item = :cd_item ' ||
							' and nvl(ie_status_item,''N'') = :ie_suspenso ';

			CALL exec_sql_dinamico_bv('VIPE', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'ds_informacoes=' || ds_informacoes_ww || ds_sep_bv_w ||
										'ie_pintar=' || ie_pintar_w || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
										'ie_tipo=DE' || ds_sep_bv_w ||
										'cd_item=' || cd_refeicao_w || ds_sep_bv_w ||
										'ie_suspenso=' ||ie_suspenso_w || ds_sep_bv_w);
		end if;
		end;
	end loop;
	close c01;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vipe_gerar_horarios_pck.vipe_sinc_diet_oral_serv ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_horarios_dietas_orais_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
