SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE vipe_gerar_horarios_pck.vipe_sincronizar_dieta_oral ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_horarios_dietas_orais_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE


	ds_sep_bv_w		varchar(50);

	nr_prescricao_w		bigint;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(15);
	cd_refeicao_w		varchar(15);
	ds_informacoes_w	varchar(2000);
	ds_informacoes_ant_w	varchar(2000);
	ds_observacoes_w	varchar(255);
	dt_suspensao_w		timestamp;
	ds_informacoes_ww	varchar(2000);
	ds_comando_update_w	varchar(4000);
	current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	dt_fim_w		timestamp;
	ie_pintar_w		varchar(1);
	ie_ciente_w		varchar(1);
	acho_w			boolean;
	dt_suspensao_item_w	timestamp;

	c01 CURSOR FOR
	SELECT	c.nr_prescricao,
		c.nr_sequencia,
		CASE WHEN c.ie_ciente='S' THEN  'C'  ELSE substr(obter_status_hor_dieta_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario),1,1) END ,
		c.cd_refeicao,
		obter_inf_dieta_adep(c.nr_prescricao,null,'DI') ds_informacoes,
		c.ds_observacao,
		c.dt_horario,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, c.nr_prescricao, c.nr_sequencia, 'D') ie_pintar,
		c.ie_ciente,
		d.dt_suspensao
	from	valor_dominio x,
		prescr_dieta d,
		prescr_dieta_hor c,
		prescr_medica a
	where	x.vl_dominio = c.cd_refeicao
	and	d.nr_prescricao = c.nr_prescricao
	and	d.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	x.cd_dominio = 99
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	((obter_se_prescr_lib_vipe(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p,c.dt_lib_horario) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and	c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((ie_data_lib_prescr_p = 'M') or (Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'))
	and	(c.cd_refeicao IS NOT NULL AND c.cd_refeicao::text <> '')
	group by
		c.dt_horario,
		c.nr_prescricao,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		c.cd_refeicao,
		c.ds_observacao,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, c.nr_prescricao, c.nr_sequencia, 'D'),
		c.dt_lib_horario,
		c.ie_ciente,
		d.dt_suspensao
	
union

	SELECT	c.nr_prescricao,
		c.nr_sequencia,
		CASE WHEN c.ie_ciente='S' THEN  'C'  ELSE substr(obter_status_hor_dieta_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario),1,1) END ,
		to_char(d.cd_dieta),
		obter_inf_dieta_adep(c.nr_prescricao,null,'DI') ds_informacoes,
		c.ds_observacao,
		c.dt_horario,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, c.nr_prescricao, c.nr_sequencia, 'D') ie_pintar,
		c.ie_ciente,
		d.dt_suspensao
	from	dieta x,
		prescr_dieta d,
		prescr_dieta_hor c,
		prescr_medica a
	where	x.cd_dieta	= d.cd_dieta
	and	d.nr_prescricao = c.nr_prescricao
	and	d.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	c.nr_seq_dieta	= d.nr_sequencia
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	((obter_se_prescr_lib_vipe(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p,c.dt_lib_horario) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and	c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((ie_data_lib_prescr_p = 'M') or (Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'))
	and	coalesce(c.cd_refeicao::text, '') = ''
	group by
		c.dt_horario,
		c.nr_prescricao,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		d.cd_dieta,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, c.nr_prescricao, c.nr_sequencia, 'D'),
		c.ds_observacao,
		c.dt_lib_horario,
		c.ie_ciente,
		d.dt_suspensao
	order by
		7;

	c02 CURSOR FOR
	SELECT	c.nr_prescricao,
		c.nr_sequencia,
		CASE WHEN c.ie_ciente='S' THEN  'C'  ELSE substr(obter_status_hor_dieta_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario),1,1) END ,
		to_char(d.cd_dieta),
		obter_inf_dieta_adep(c.nr_prescricao,null,'DI') ds_informacoes,
		c.ds_observacao,
		c.dt_suspensao,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, c.nr_prescricao, c.nr_sequencia, 'D') ie_pintar,
		c.ie_ciente
	from	prescr_dieta d,
		prescr_dieta_hor c,
		prescr_medica a
	where	d.nr_prescricao = c.nr_prescricao
	and	d.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr  between dt_validade_limite_p and dt_fim_w
	and	obter_se_prescr_lib_vipe(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, c.dt_lib_horario) = 'S'
	and	obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((ie_data_lib_prescr_p = 'M') or (Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'))
	group by
		c.nr_prescricao,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		d.cd_dieta,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, c.nr_prescricao, c.nr_sequencia, 'D'),
		c.ds_observacao,
		c.dt_lib_horario,
		c.ie_ciente
	
union all

	SELECT	a.nr_prescricao,
		d.nr_sequencia,
		CASE WHEN d.ie_ciente='S' THEN  'C'  ELSE substr(obter_status_hor_dieta_oral(null,d.dt_suspensao),1,1) END ,
		to_char(d.cd_dieta),
		obter_inf_dieta_adep(a.nr_prescricao,null,'DI') ds_informacoes,
		null ds_observacao,
		d.dt_suspensao,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, d.nr_prescricao, d.nr_sequencia, 'D') ie_pintar,
		d.ie_ciente
	from	dieta x,
		prescr_dieta d,
		prescr_medica a
	where	x.cd_dieta = d.cd_dieta
	and	d.nr_prescricao	= a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr  between dt_validade_limite_p and dt_fim_w
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(d.dt_suspensao::text, '') = ''))
	and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '') or (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> ''))
	and	a.dt_inicio_prescr between dt_inicial_horarios_p and dt_final_horarios_p
	and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
	and	not exists (
			select	1
			from	prescr_dieta_hor c
			where	c.nr_prescricao = d.nr_prescricao
			and	c.nr_prescricao = a.nr_prescricao
			and	((ie_data_lib_prescr_p = 'M') or (Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S')))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	group by
		a.nr_prescricao,
		d.cd_dieta,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, d.nr_prescricao, d.nr_sequencia, 'D'),
		x.nm_dieta,
		d.nr_sequencia,
		d.dt_suspensao,
		d.cd_intervalo,
		d.ie_ciente
	order by
		dt_suspensao;

	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	dt_fim_w	:= vipe_gerar_horarios_pck.getdtfimvalidade(dt_validade_limite_p);

	if (ie_horarios_dietas_orais_p <> 'N') then
		begin
		open c01;
		loop
		fetch c01 into	nr_prescricao_w,
				nr_seq_horario_w,
				ie_status_horario_w,
				cd_refeicao_w,
				ds_informacoes_w,
				ds_observacoes_w,
				current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp,
				ie_pintar_w,
				ie_ciente_w,
				dt_suspensao_item_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			nr_horario_w := vipe_gerar_horarios_pck.obter_posicao_horario(current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp);

			if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
				ds_informacoes_ww	:= substr(ds_informacoes_w,1,2000);

				if (dt_suspensao_item_w IS NOT NULL AND dt_suspensao_item_w::text <> '') and (ie_status_horario_w	in ('N','S')) then
					ie_status_horario_w	:= 'K';
				end if;

				ds_comando_update_w	:=	' update w_vipe_t ' ||
								' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
								' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
								' ds_diluicao = substr(ds_diluicao|| :ds_informacoes,1,2000), ' ||
								' ie_pintar = :ie_pintar, ' ||
								' ie_ciencia = :ie_ciencia ' ||
								' where nm_usuario = :nm_usuario ' ||
								' and ie_tipo_item = :ie_tipo ' ||
								' and cd_item = :cd_item ';

				CALL exec_sql_dinamico_bv('VIPE', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
											'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
											'ds_informacoes=' || ds_informacoes_ww || ds_sep_bv_w ||
											'ie_pintar=' || ie_pintar_w || ds_sep_bv_w ||
											'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
											'ie_tipo=D' || ds_sep_bv_w ||
											'cd_item=' || cd_refeicao_w || ds_sep_bv_w ||
											'ie_ciencia=' || ie_ciente_w || ds_sep_bv_w);
			end if;
			end;
		end loop;
		close c01;
		end;
	else
		begin
		open c02;
		loop
		fetch c02 into	nr_prescricao_w,
				nr_seq_horario_w,
				ie_status_horario_w,
				cd_refeicao_w,
				ds_informacoes_w,
				ds_observacoes_w,
				dt_suspensao_w,
				ie_pintar_w,
				ie_ciente_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			ds_informacoes_ww	:= substr(ds_informacoes_w || ' - Obs: ' || ds_observacoes_w,1,2000);

			ds_comando_update_w	:=	' update w_vipe_t ' ||
							' set nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
							' ds_diluicao = :ds_informacoes, ' ||
							' ie_pintar = :ie_pintar, ' ||
							' ie_ciencia = :ie_ciencia ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and cd_item = :cd_item ';

			CALL exec_sql_dinamico_bv('VIPE', ds_comando_update_w,	'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'ds_informacoes=' || ds_informacoes_ww || ds_sep_bv_w ||
										'ie_pintar=' || ie_pintar_w || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
										'ie_tipo=D' || ds_sep_bv_w ||
										'cd_item=' || cd_refeicao_w || ds_sep_bv_w ||
										'ie_ciencia=' || ie_ciente_w || ds_sep_bv_w);
			end;
		end loop;
		close c02;
		end;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vipe_gerar_horarios_pck.vipe_sincronizar_dieta_oral ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_horarios_dietas_orais_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
