CREATE OR REPLACE PROCEDURE vipe_gerar_horarios_pck.vipe_sincronizar_supl_oral ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE


	ds_sep_bv_w		varchar(50);

	nr_prescricao_w		bigint;
	nr_seq_material_w	integer;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(1);
	cd_material_w		integer;
	ds_material_w		varchar(255);
	ie_acm_sn_w		varchar(1);
	cd_intervalo_w		varchar(7);
	qt_dose_w		double precision;
	ds_prescricao_w		varchar(100);
	ie_status_w		varchar(1);
	ie_utiliza_serv_w varchar(1) := vipe_gerar_horarios_pck.get_ie_utiliza_serv_dieta();
	ds_comando_update_w	varchar(4000);

	current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	dt_fim_w		timestamp;
	ie_pintar_w		varchar(1);
	ie_ciente_w		varchar(1);
	cd_setor_atendimento_w	bigint;

	c01 CURSOR FOR
	SELECT	a.nr_prescricao,
		c.nr_seq_material,
		c.nr_sequencia,
		CASE WHEN coalesce(x.dt_suspensao::text, '') = '' THEN 			CASE WHEN c.ie_ciente='S' THEN  'C'  ELSE CASE WHEN ie_utiliza_serv_w='S' THEN 					CASE WHEN coalesce(s.dt_liberacao::text, '') = '' THEN 						CASE WHEN coalesce(c.dt_lib_horario::text, '') = '' THEN 							substr(obter_status_hor_supl_oral_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario),1,1)  ELSE 'Y' END 						  ELSE substr(obter_status_hor_supl_oral_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario),1,1) END   ELSE substr(obter_status_hor_supl_oral_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario),1,1) END  END   ELSE CASE WHEN coalesce(c.dt_fim_horario::text, '') = '' THEN 'K'  ELSE 'A' END  END ,
		c.cd_material,
		obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,
		x.cd_intervalo,
		x.qt_dose,
		substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
		coalesce(x.ie_suspenso,'N') ie_suspenso,
		c.dt_horario,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, x.nr_prescricao, c.nr_seq_material, 'S') ie_pintar,
		c.ie_ciente
	FROM prescr_material x, prescr_medica a, prescr_mat_hor c
LEFT OUTER JOIN nut_atend_serv_dia s ON (c.nr_sequencia = s.nr_Seq_horario)
WHERE x.nr_prescricao		= c.nr_prescricao and x.nr_sequencia		= c.nr_seq_material and x.nr_prescricao		= a.nr_prescricao and c.nr_prescricao		= a.nr_prescricao  and Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S' and obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S' and a.nr_atendimento	= nr_atendimento_p and a.dt_validade_prescr	between dt_validade_limite_p and dt_fim_w and ((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S'))) and x.ie_agrupador		= 12 and ((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = '')) and coalesce(c.ie_situacao,'A')	= 'A' and ((ie_utiliza_serv_w <> 'S') or (s.nr_seq_horario IS NOT NULL AND s.nr_seq_horario::text <> '')) and c.ie_agrupador		= 12 and ((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> '')) and ((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = '')) and ((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = '')) and ((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SO',
																		cd_estabelecimento_p,
																		cd_setor_usuario_p,
																		cd_perfil_p,
																		c.cd_material,
																		null,
																		null,
																		null,
																		a.cd_setor_atendimento,
																		null,
																		null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e n√£o haviam passado nada
																		null) = 'S')))  -- nr_seq_exame_p
  and c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w and ((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_atendimento_w)) and ((ie_data_lib_prescr_p = 'M') or (Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S')) group by
		c.dt_horario,
		a.nr_prescricao,
		c.nr_seq_material,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		c.cd_material,
		x.ie_acm,
		x.ie_se_necessario,
		VIPE_obter_se_colore(a.ie_motivo_prescricao, x.nr_prescricao, c.nr_seq_material, 'S'),
		x.cd_intervalo,
		x.qt_dose,
		x.dt_suspensao,
		x.ie_suspenso,
		c.dt_lib_horario,
		s.dt_liberacao,
		c.ie_ciente
	order by
		c.dt_horario,
		c.dt_suspensao;

	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	dt_fim_w	:= vipe_gerar_horarios_pck.getdtfimvalidade(dt_validade_limite_p);
	cd_setor_atendimento_w	:= vipe_gerar_horarios_pck.obter_setor_atend(nr_atendimento_p);
	open c01;
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_material_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_material_w,
			ie_acm_sn_w,
			cd_intervalo_w,
			qt_dose_w,
			ds_prescricao_w,
			ie_status_w,
			current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp,
			ie_pintar_w,
			ie_ciente_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := vipe_gerar_horarios_pck.obter_posicao_horario(current_setting('vipe_gerar_horarios_pck.dt_horario_w')::timestamp);

		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			ds_comando_update_w	:=	' update w_vipe_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
							' ie_pintar = :ie_pintar, ' ||
							' ie_ciencia = :ie_ciencia ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||
							' and cd_item = :cd_item ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and nvl(ie_status_item,''N'') = :ie_status ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ';

			CALL exec_sql_dinamico_bv('VIPE', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'ie_pintar=' || ie_pintar_w || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
										'ie_tipo=S' || ds_sep_bv_w ||
										'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
										'ie_status=' || ie_status_w || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w ||
										'ie_ciencia=' || ie_ciente_w || ds_sep_bv_w);
		end if;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vipe_gerar_horarios_pck.vipe_sincronizar_supl_oral ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
