SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION wheb_assist_pck.get_cd_certificado () RETURNS varchar AS $body$
BEGIN
		if (not current_setting('wheb_assist_pck.ie_atualizou_cerificado_w')::boolean) then
			select	max(cd_certificado)
			into STRICT	current_setting('wheb_assist_pck.cd_certificado_w')::varchar(100)
			from	usuario
			where	nm_usuario = current_setting('wheb_assist_pck.nm_usuario_w')::varchar(50);
			PERFORM set_config('wheb_assist_pck.ie_atualizou_cerificado_w', true, false);
		end if;
	return current_setting('wheb_assist_pck.cd_certificado_w')::varchar(100);
	end;


	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
