CREATE OR REPLACE FUNCTION wheb_assist_pck.get_se_gera_assinatura () RETURNS boolean AS $body$
DECLARE

	ie_assinatura_w	varchar(10);
	ie_consultar_w	varchar(10);
	ie_alterar_w	varchar(10);
	ds_mensagem_w	varchar(255);
	ds_sucesso_w	varchar(255);
	ie_imprimir_w	smallint;
	ie_nivel_log_w	smallint;
	cd_relatorio_w	integer;
	cd_classif_relat_w	varchar(4);
	ie_permite_lote_w	tasy_proj_assin_perfil.ie_permite_lote%type;
	
BEGIN
	
	if (upper(get_ds_programa) = upper('Tasy JAVA')) then	
		return false;
	end if;	

	if (get_cd_certificado IS NOT NULL AND get_cd_certificado::text <> '') or (get_gerar_sem_certificado	= 'S') then

		if (coalesce(ie_assinatura_w::text, '') = '') then
			obter_regra_assinatura_perfil(	get_nr_seq_projeto_ass,
											current_setting('wheb_assist_pck.cd_perfil_w')::bigint,
											ie_assinatura_w,
											ie_consultar_w,
											ie_alterar_w,
											current_setting('wheb_assist_pck.ds_diretorio_w')::varchar(255),
											current_setting('wheb_assist_pck.nm_arquivo_w')::varchar(255),
											current_setting('wheb_assist_pck.ie_gerar_pdf_w')::varchar(255),
											current_setting('wheb_assist_pck.ie_solic_senha_w')::varchar(255),
											ds_mensagem_w,
											ds_sucesso_w,
											ie_imprimir_w,
											ie_nivel_log_w,
											cd_relatorio_w,
											cd_classif_relat_w,
											ie_permite_lote_w);
		end if;

		return(ie_assinatura_w <> 'N');

	end if;

	return false;
	end;



$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
