CREATE OR REPLACE FUNCTION wheb_assist_pck.getescalaliberada ( ie_escala_p bigint) RETURNS varchar AS $body$
BEGIN
	
	if (current_setting('wheb_assist_pck.regraescala_w')::regraEscalaVetor.count	= 0) then
		CALL wheb_assist_pck.carregarpermissaoescala();
	end if;
	
	
	for i in 1..regraEscala_w.count -1 loop
		begin
		if (current_setting('wheb_assist_pck.regraescala_w')::regraEscalaVetor[i].ie_escala_w	= ie_escala_p) then
			return current_setting('wheb_assist_pck.regraescala_w')::regraEscalaVetor[i].ie_permite_w;
		end if;
		end;
	end loop;
	
	return 'S';
	
	end;
	

$body$
