SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION wheb_assist_pck.getliberacaoescalaestab ( ie_escala_p bigint) RETURNS varchar AS $body$
DECLARE

	qt_reg_w	bigint;
	cd_cgc_cliente_w	varchar(255)	:= wheb_assist_pck.getcgcestabelecimento();
	
BEGIN
	


        select sum(qt_liberacao)
        into STRICT	qt_reg_w	    








        from (
                SELECT	coalesce(max(1), 0) qt_liberacao
                from	escala_liberacao
                where	IE_ESCALA	= ie_escala_p

                and	CD_CGC_CLIENTE = cd_cgc_cliente_w
                and     coalesce(IE_LIBERACAO_FILIAL,'S') = 'S'
                
union all
 
                SELECT	coalesce(max(1), 0) qt_liberacao
                from	escala_liberacao
                where	IE_ESCALA	= ie_escala_p
                and     coalesce(IE_LIBERACAO_FILIAL,'S') = 'N'              
                and	substr(CD_CGC_CLIENTE,1, 8) = substr(cd_cgc_cliente_w,1,8)



                ) x;
	
	if (qt_reg_w	= 0) then
		return 'N';
	end if;


	
	return 'S';
	
	end;
	
	
	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
