CREATE OR REPLACE FUNCTION wheb_db.getcomma_table_columns (nm_tabela_p text, alias_p text DEFAULT NULL) RETURNS varchar AS $body$
DECLARE

	alias_w varchar(31);
	
BEGIN
	alias_w := alias_p;
