CREATE OR REPLACE PROCEDURE wheb_exportar_hl7_long_pck.gerar_hl7_segmento_group (ar_hl7_elemento_w hl7Segmento,ar_result_query_sup_p myArray, ds_valor_long_p INOUT DBMS_SQL.VARCHAR2A) AS $body$
BEGIN
		if (ar_hl7_elemento_w[1].ie_tipo = 'GRU') then
			ds_valor_long_p := wheb_exportar_hl7_long_pck.gerar_hl7_grupo(ar_hl7_elemento_w, ar_result_query_sup_p, ds_valor_long_p);
		elsif (ar_hl7_elemento_w[1].ie_tipo  = 'SEG') then
			ds_valor_long_p := wheb_exportar_hl7_long_pck.gerar_hl7_segmento_s(ar_hl7_elemento_w, ar_result_query_sup_p, ds_valor_long_p);
		elsif (ar_hl7_elemento_w[1].ie_tipo  = 'DTC') then
			ds_valor_long_p := wheb_exportar_hl7_long_pck.gerar_hl7_data_type_c(ar_hl7_elemento_w, ar_result_query_sup_p, ds_valor_long_p);
		end if;
	end;

	 /*
	  Grupos são segmentos organizadores no HL7.
	  Pode agrupar outros grupos e/ou segmentos.
	  Pode operar como orquestrador de repetição.
	  */
$body$
LANGUAGE PLPGSQL
;
