CREATE OR REPLACE PROCEDURE wheb_exportar_hl7_long_pck.gerar_hl7_segmento_s (ar_hl7_elemento_w hl7Segmento,ar_result_query_sup_p myArray, ds_valor_long_p INOUT DBMS_SQL.VARCHAR2A) AS $body$
DECLARE

  ds_valor_long_w DBMS_SQL.VARCHAR2A;
	
BEGIN
    ds_valor_long_w.delete;
		ds_valor_long_w := wheb_exportar_hl7_long_pck.gerar_hl7_atributo(ar_hl7_elemento_w, ar_result_query_sup_p, ds_valor_long_w);
    if ds_valor_long_w.count > 0 then
		  for p in 1..ds_valor_long_p.count loop
        CALL CALL wheb_exportar_hl7_long_pck.addhl7(ds_valor_long_p(p));
      end loop;
      CALL CALL wheb_exportar_hl7_long_pck.addhl7(substr(ar_hl7_elemento_w[1].nm_segmento,1,3) || current_setting('wheb_exportar_hl7_long_pck.ds_separador_w')::varchar(1));
      for p in 1..ds_valor_long_w.count loop
        CALL CALL wheb_exportar_hl7_long_pck.addhl7(ds_valor_long_w(p));
      end loop;
      CALL CALL wheb_exportar_hl7_long_pck.addhl7(chr(13));
    end if;
	end;

	 /*
	  Tipos compostos são informações agrupadas no HL7.
	  Pode agrupar tipos simples ou compostos.
	  Pode haver apenas um tipo coposto dentro de outro, não mais.
	  Pode operar como orquestrador de repetição.
