CREATE OR REPLACE FUNCTION wheb_exportar_hl7_long_pck.obter_valor_parametro (nm_parametro_p text, ar_parametros_p myArray) RETURNS text AS $body$
DECLARE

	ds_valor text;
	
BEGIN
		for p in 1..ar_parametros_p.count loop
