CREATE OR REPLACE PROCEDURE wheb_exportar_hl7_pck.gerar_hl7_segmento_s (ar_hl7_elemento_w hl7Segmento,ar_result_query_sup_p myArray, ds_valor_p INOUT text) AS $body$
DECLARE

	t_ds_valor_w varchar(32000);
	
BEGIN
		t_ds_valor_w := wheb_exportar_hl7_pck.gerar_hl7_atributo(ar_hl7_elemento_w, ar_result_query_sup_p, t_ds_valor_w);
		if (t_ds_valor_w IS NOT NULL AND t_ds_valor_w::text <> '') then
			CALL CALL wheb_exportar_hl7_pck.addhl7(ds_valor_p  || substr(ar_hl7_elemento_w[1].nm_segmento,1,3) || current_setting('wheb_exportar_hl7_pck.ds_separador_w')::varchar(1) || t_ds_valor_w || chr(13));
		end if;
	end;

	 /*
	  Tipos compostos são informações agrupadas no HL7.
	  Pode agrupar tipos simples ou compostos. 
