CREATE OR REPLACE PROCEDURE wheb_hl7_pck.copiar_dados_hl7_atributo (nr_seq_segmento_p bigint, nr_seq_segmento_new_p bigint) AS $body$
DECLARE

        c01 CURSOR FOR
            SELECT nr_sequencia,
                   nr_seq_apresentacao,
                   nm_atributo,
                   nm_atributo_hl7,
                   nr_seq_atrib_segm
              from hl7_atributo
             where nr_seq_segmento = nr_seq_segmento_p
             order by nr_seq_apresentacao;

        c02 CURSOR(nm_atributo_hl7_pc text) FOR
            SELECT nr_seq_atrib_segm
              from hl7_atributo
             where nr_seq_segmento = nr_seq_segmento_new_p
               and nm_atributo_hl7 = nm_atributo_hl7_pc;

        hl7_atributo_w      c01%rowtype;
        nr_seq_atrib_segm_w bigint;

    
BEGIN
        open c01;
        loop
            fetch c01
                into hl7_atributo_w;
            EXIT WHEN NOT FOUND; /* apply on c01 */
            begin
                if (hl7_atributo_w.nm_atributo IS NOT NULL AND hl7_atributo_w.nm_atributo::text <> '') then
                    update hl7_atributo
                       set nm_atributo = hl7_atributo_w.nm_atributo
                     where nr_seq_segmento = nr_seq_segmento_new_p
                       and nm_atributo_hl7 = hl7_atributo_w.nm_atributo_hl7;
                end if;
                if (hl7_atributo_w.nr_seq_atrib_segm IS NOT NULL AND hl7_atributo_w.nr_seq_atrib_segm::text <> '') then
                    open c02(hl7_atributo_w.nm_atributo_hl7);
                    fetch c02
                        into nr_seq_atrib_segm_w;
                    if (c02%found) then
                        CALL wheb_hl7_pck.copiar_dados_hl7_segmento(hl7_atributo_w.nr_seq_atrib_segm, nr_seq_atrib_segm_w);
                    end if;
                    close c02;
                end if;
            end;
        end loop;
        close c01;
    end;


$body$
LANGUAGE PLPGSQL
;
