SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE wheb_hl7_pck.validate_order_id (nr_order_id_p bigint, ds_result_p INOUT text) AS $body$
DECLARE

        nr_order_id_w bigint;

    
BEGIN
    
        select nr_prescricao into STRICT nr_order_id_w from prescr_procedimento where nr_seq_interno = nr_order_id_p;
    exception
        when no_data_found then
            nr_order_id_w := 0;

            if (coalesce(nr_order_id_w::text, '') = '' or nr_order_id_w <= 0) then
                ds_result_p := 'Invalid order id';
            end if;
    end;


$body$
LANGUAGE PLPGSQL
;
