CREATE OR REPLACE FUNCTION wheb_inconsistencia.get_config_auto (integridade_referencial_p INTEGRIDADE_REFERENCIAL) RETURNS TASY_CONFIG_INCONSISTENCIA%ROWTYPE AS $body$
DECLARE

    tasy_config_inconsistencia_w TASY_CONFIG_INCONSISTENCIA%rowtype;

BEGIN
    BEGIN
        SELECT *
          INTO STRICT tasy_config_inconsistencia_w
          FROM TASY_CONFIG_INCONSISTENCIA
         WHERE NM_TABELA                  = integridade_referencial_p.nm_tabela
           AND NM_INTEGRIDADE_REFERENCIAL = integridade_referencial_p.nm_integridade_referencial
           AND coalesce(NM_INDICE::text, '') = '' 
      ORDER BY NR_SEQUENCIA LIMIT 1;

    EXCEPTION
      WHEN no_data_found THEN
        tasy_config_inconsistencia_w := NULL;
    END;

    RETURN tasy_config_inconsistencia_w;
  END;


