CREATE OR REPLACE PROCEDURE wheb_instrumentation.log_add (l_category text, l_group text, l_level bigint, l_text text, l_type text) AS $body$
DECLARE

    rec LogRecord;

BEGIN
    PERFORM set_config('wheb_instrumentation.internal_seq', current_setting('wheb_instrumentation.internal_seq')::bigint + 1, false);

    rec.L_CATEGORY        := wheb_instrumentation.format_category(l_category);
    rec.L_DATE            := CURRENT_TIMESTAMP;
    rec.L_GROUP           := wheb_instrumentation.format_group(l_group);
    rec.L_LEVEL           := wheb_instrumentation.format_level(l_level);
    rec.L_SEQ             := current_setting('wheb_instrumentation.internal_seq')::bigint;
    rec.L_TEXT            := wheb_instrumentation.format_text(l_text);
    rec.L_TYPE            := wheb_instrumentation.format_type(l_type);
    rec.L_CALL_STACK      := wheb_instrumentation.format_stack(DBMS_UTILITY.FORMAT_CALL_STACK);
    rec.L_ERROR_BACKTRACE := NULL;
    rec.L_ERROR_STACK     := NULL;

    IF l_type IN (current_setting('wheb_instrumentation.type_error')::varchar(12), current_setting('wheb_instrumentation.type_warning')::varchar(12)) THEN
      rec.L_ERROR_BACKTRACE := wheb_instrumentation.format_stack(DBMS_UTILITY.FORMAT_ERROR_BACKTRACE);
      rec.L_ERROR_STACK     := wheb_instrumentation.format_stack(DBMS_UTILITY.FORMAT_ERROR_STACK);
    END IF;

    current_setting('wheb_instrumentation.internal_log')::LogTable.extend;
    current_setting('wheb_instrumentation.internal_log')::LogTable(current_setting('wheb_instrumentation.internal_log')::LogTable.COUNT) := rec;
  END;

