SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE wheb_mensagem_pck.carregar_mensagem (nr_seq_mensagem_p bigint) AS $body$
BEGIN
		select	max(obter_desc_expressao(cd_exp_informacao, ds_informacao))
		into STRICT	current_setting('wheb_mensagem_pck.ds_mensagem_w')::varchar(32766)
		from	dic_objeto
		where	nr_sequencia = nr_seq_mensagem_p
		and	ie_tipo_objeto = 'T';

		current_setting('wheb_mensagem_pck.mensagens_usuario_w')::Vetor(current_setting('wheb_mensagem_pck.mensagens_usuario_w')::Vetor.count+1).nr_seq_mensagem := nr_seq_mensagem_p;
		current_setting('wheb_mensagem_pck.mensagens_usuario_w')::Vetor(current_setting('wheb_mensagem_pck.mensagens_usuario_w')::Vetor.count).ds_mensagem := current_setting('wheb_mensagem_pck.ds_mensagem_w')::varchar(32766);
	exception
	when others then
		RAISE EXCEPTION '%', 'Ocorreu uma excecao ao obter a mensagem correspondente ao codigo ' || to_char(nr_seq_mensagem_p) || ', favor verificar.' || current_setting('wheb_mensagem_pck.var_char_trat_erro_w')::varchar(4) USING ERRCODE = '45011';
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
