SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE wheb_mensagem_pck.get_mensagem ( nr_seq_mensagem_p bigint) AS $body$
DECLARE

	ie_achou_w boolean := false;
	
BEGIN

	PERFORM set_config('wheb_mensagem_pck.ds_mensagem_w', '', false);

	if (current_setting('wheb_mensagem_pck.var_char_trat_erro_w')::coalesce(varchar(4)::text, '') = '' ) then
		PERFORM set_config('wheb_mensagem_pck.var_char_trat_erro_w', obter_separador_bv, false);
	end if;
	if ( current_setting('wheb_mensagem_pck.mensagens_usuario_w')::Vetor.count = 0	) then
		CALL wheb_mensagem_pck.carregar_mensagem(nr_seq_mensagem_p);
	else
		for j in 1..mensagens_usuario_w.count loop
			if ( not ie_achou_w ) and ( current_setting('wheb_mensagem_pck.mensagens_usuario_w')::Vetor[j].nr_seq_mensagem = nr_seq_mensagem_p) then
				ie_achou_w 	:= true;
				PERFORM set_config('wheb_mensagem_pck.ds_mensagem_w', current_setting('wheb_mensagem_pck.mensagens_usuario_w')::Vetor[j].ds_mensagem, false);
			end if;
		end loop;

		if ( not ie_achou_w) then
			CALL wheb_mensagem_pck.carregar_mensagem(nr_seq_mensagem_p);
		end if;
	end if;
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
