CREATE OR REPLACE FUNCTION wheb_mensagem_pck.get_texto ( nr_seq_mensagem_p bigint, Vl_Macros_P text default null) RETURNS varchar AS $body$
DECLARE

	
	ds_retorno_w	varchar(32766);
	
BEGIN

	CALL wheb_mensagem_pck.get_mensagem(nr_seq_mensagem_p);	
