		CALL CALL wheb_mensagem_pck.substituir_macros_mensagem(vl_macros_p);
		ds_retorno_w	:= current_setting('wheb_mensagem_pck.ds_mensagem_w')::varchar(32766) || current_setting('wheb_mensagem_pck.var_char_trat_erro_w')::varchar(4);
	else
		ds_retorno_w	:= current_setting('wheb_mensagem_pck.ds_mensagem_w')::varchar(32766);
		
	end if;
	
	return	replace(ds_retorno_w, '#@#@', '');
	END;

