SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE wheb_performance_pck.gera_plano_execucao () AS $body$
DECLARE

	qt_valor_w		bigint;
	ds_indicador_w		varchar(50);

	C01 CURSOR FOR
		SELECT 	sum(b.value),
			'consistent gets'
		from 	v$statname a,
			v$mystat b
		where 	a.statistic#=b.statistic#
		and	a.name in ('db block gets','consistent gets')
		
union

		SELECT 	b.value,
			a.name
		from 	v$statname a,
			v$mystat b
		where 	a.statistic#=b.statistic#
		and	a.name in ('recursive calls','physical reads');
	
BEGIN

	open C01;
	loop
	fetch C01 into
		qt_valor_w,
		ds_indicador_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ds_indicador_w = 'consistent gets') then
			PERFORM set_config('wheb_performance_pck.qt_blocos_logico_w', qt_valor_w, false);
		elsif (ds_indicador_w = 'recursive calls') then
			PERFORM set_config('wheb_performance_pck.qt_chamada_recursiva_w', qt_valor_w, false);
		elsif (ds_indicador_w = 'physical reads') then
			PERFORM set_config('wheb_performance_pck.qt_leitura_fisica_w', qt_valor_w, false);
		end if;
		end;
	end loop;
	close C01;
	end;


$body$
LANGUAGE PLPGSQL
;
-- REVOKE ALL ON PROCEDURE wheb_performance_pck.gera_plano_execucao () FROM PUBLIC;
