CREATE OR REPLACE FUNCTION wheb_rtf_pck.get_texto_rtf (ds_texto_p text) RETURNS varchar AS $body$
BEGIN
		if (substr(ds_texto_p,1,5) <> '{\rtf') then
			return get_cabecalho ||chr(13)||chr(10)|| replace(ds_texto_p, chr(13)||chr(10), chr(13)||chr(10)|| get_quebra_linha) ||chr(13)||chr(10)|| wheb_rtf_pck.get_rodape();
		else
			return ds_texto_p;
		end if;
	end;

$body$
LANGUAGE PLPGSQL
 STABLE;
