SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE wheb_seguranca.padstring (p_text INOUT text) AS $body$
DECLARE

	l_units  bigint;
	
BEGIN
		IF LENGTH(p_text) MOD 8 > 0 THEN
			l_units := TRUNC(LENGTH(p_text)/8) + 1;
			p_text  := RPAD(p_text, l_units * 8, current_setting('wheb_seguranca.g_pad_chr')::varchar(1));
		END IF;
	END;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
