CREATE OR REPLACE PROCEDURE wsuite_login_pck.wsuite_update_user_wsuite ( id_subject_p wsuite_usuario.id_subject%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, ds_replacement_login_p wsuite_usuario.nm_usuario_web%type, ds_login_p wsuite_usuario.ds_login%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finality: Insert user on the Web Suite users table, it is necessary to control users on the Tasy System
