BEGIN

update	wsuite_usuario
set	nm_usuario 		= nm_usuario_p,
	dt_atualizacao 		= clock_timestamp(),
	cd_pessoa_fisica 	= coalesce(cd_pessoa_fisica_p, cd_pessoa_fisica),
	id_subject		= id_subject_p
where	ds_login		= ds_login_p
and	nm_usuario_web		= ds_replacement_login_p;

commit;

END;

