CREATE OR REPLACE FUNCTION wsuite_util_pck.get_texto ( nr_seq_mensagem_p bigint, Vl_Macros_P text DEFAULT NULL) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w varchar(32766);
    ds_mensagem_w        varchar(32766);
    var_char_trat_erro_w varchar(4);

BEGIN
    ds_mensagem_w   := wsuite_util_pck.get_wsuite_expression(nr_seq_mensagem_p);
