SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION wsuite_util_pck.get_wsuite_expression ( nr_seq_expression_param bigint, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type default null) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Returns expressions of wsuite
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ x] Outros: Web suite
 ------------------------------------------------------------------------------------------------------------------
Caution: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
			

rec 			varchar(400);
ds_locale_tws_w		establishment_locale.ds_locale_tws%type;
nr_seq_idioma_w		tasy_idioma.nr_sequencia%type := 5;




BEGIN


select 	max(ds_locale_tws)
into STRICT	ds_locale_tws_w
from 	establishment_locale a
where 	a.cd_estabelecimento = coalesce(cd_estabelecimento_p, 1);


if (ds_locale_tws_w IS NOT NULL AND ds_locale_tws_w::text <> '') then
	select 	nr_sequencia
	into STRICT	nr_seq_idioma_w
	from 	tasy_idioma
	where 	ds_language_tag = ds_locale_tws_w;
end if;

/* Se não achar tradução para o idioma configurado carrega o idioma pt_BR por padrão */

select 	coalesce(obter_desc_expressao_idioma( nr_seq_expression_param, null, nr_seq_idioma_w),
	obter_desc_expressao_idioma( nr_seq_expression_param, null, 1))
into STRICT	rec
;

return rec;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
