SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION wsuite_util_pck.get_wsuite_user_locale ( user_login text) RETURNS SETOF WSUITE_LOCALE AS $body$
DECLARE

					
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Returns locale, person code and establishment
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ x] Outros: Web suite
 ------------------------------------------------------------------------------------------------------------------
Caution: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
				
rec 	wsuite_locale_record;


BEGIN
	select (select cd_pessoa_fisica 
		from  wsuite_usuario 
		where ds_login = user_login) as user_code, 
		coalesce((select ds_locale_tws from  establishment_locale where cd_estabelecimento = a.cd_estabelecimento), 'pt_BR') as user_locale, 
		a.cd_estabelecimento user_stab 
	into STRICT 	rec
	from    wsuite_configuracao a 
	where   (a.cd_estabelecimento IS NOT NULL AND a.cd_estabelecimento::text <> '');

	RETURN NEXT rec;
	
	return;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
