CREATE OR REPLACE PROCEDURE xdok_json_pck.get_episodios_add_log_msg_xdok ( nr_episodios_p text ) AS $body$
DECLARE


episodios CURSOR FOR
SELECT  b.nr_sequencia nr_seq_episodio
from    episodio_paciente b
where   b.nr_sequencia in (WITH RECURSIVE cte AS (
 select  trim(both regexp_substr(nr_episodios_p, '[^,]+', 1, level)) valor

                            level <= length(nr_episodios_p) - length(replace(nr_episodios_p, ',', '')) + 1  UNION ALL
 select  trim(both regexp_substr(nr_episodios_p, '[^,]+', 1, level)) valor
                            
                            level <= length(nr_episodios_p) - length(replace(nr_episodios_p, ',', '')) + 1 JOIN cte c ON ()

) SELECT * FROM cte;
);

ds_json_w text;
BEGIN
    for episodio in episodios loop
    begin
        ds_json_w := xdok_json_pck.get_json_xdok(episodio.nr_seq_episodio);
    end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
