CREATE OR REPLACE FUNCTION xdok_json_pck.get_sistema_externo ( cd_cgc_p text, cd_estabelecimento_p bigint default null) RETURNS varchar AS $body$
DECLARE

cd_sistema_ant_w   pessoa_juridica.cd_sistema_ant%type;
cd_cgc_w           pessoa_juridica.cd_cgc%type;

BEGIN
    if ( coalesce(cd_cgc_p::text, '') = '' ) then
        begin
        select  cd_cgc
        into STRICT    cd_cgc_w
        from    estabelecimento
        where   cd_estabelecimento = cd_estabelecimento_p;
        exception
        when others then
            cd_cgc_w := null;
        end;
    else
        cd_cgc_w := cd_cgc_p;
    end if;

    begin
    select  ds_nome_abrev
    into STRICT    cd_sistema_ant_w
    from    pessoa_juridica
    where   cd_cgc = cd_cgc_w;
    exception
    when others then
        cd_sistema_ant_w := null;
    end;

return cd_sistema_ant_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
